import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Menu, Dropdown } from 'semantic-ui-react';
import { useAuth } from '../context/AuthContext';

function Navbar() {
  const { getUser, userIsAuthenticated, userLogout } = useAuth()

  const logout = () => {
    userLogout()
  }

  const enterMenuStyle = () => {
    return userIsAuthenticated() ? { "display": "none" } : { "display": "block" }
  }

  const logoutMenuStyle = () => {
    return userIsAuthenticated() ? { "display": "block" } : { "display": "none" }
  }

  const adminPageStyle = () => {
    const user = getUser()
    return user && user.data.rol[0] === 'ADMIN' ? { "display": "block" } : { "display": "none" }
  }

  const getUserName = () => {
    const user = getUser()
    return user ? user.data.first_name : ''
  }

  return (
<Menu inverted color='teal' stackable size='massive' style={{ borderRadius: 0 }}>
      <Container>
        <Menu.Menu position='left'>
          <Menu.Item header>Theater Schedule</Menu.Item>
          <Menu.Item as={Link} exact='true' to="/about">О системе</Menu.Item>
          <Menu.Item as={Link} to="/userpage" style={{ display: userIsAuthenticated() ? 'block' : 'none' }}>Просмотр расписания</Menu.Item>
          <Menu.Item as={Link} to="/adminpage" style={{ display: userIsAuthenticated() && getUser().data.rol[0] === 'ADMIN' ? 'block' : 'none' }}>Управление расписанием</Menu.Item>
        </Menu.Menu>
        <Menu.Menu position='right'>
          {!userIsAuthenticated() && (
            <>
              <Menu.Item as={Link} to="/login">Войти</Menu.Item>
              <Menu.Item as={Link} to="/registration">Регистрация</Menu.Item>
            </>
          )}
          {userIsAuthenticated() && (
            <Dropdown text={`Здравствуйте, ${getUserName()}`} pointing className='link item'>
              <Dropdown.Menu>
                <Dropdown.Item as={Link} exact='true' to="/account/settings">Настройки</Dropdown.Item>
                <Dropdown.Item as={Link} exact='true' to="/account/help">Помощь</Dropdown.Item>
                <Dropdown.Item as={Link} to="/login" onClick={logout}>Выход</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Menu.Menu>
      </Container>
    </Menu>
  )
}

export default Navbar
