import React, { useState } from 'react'
import { Navigate } from 'react-router-dom'
import { Container } from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { orderApi } from '../misc/OrderApi'
import { handleLogError } from '../misc/Helpers'
import AccountSettingsTab from './AccountSettingsTab'

function AccountSettingsPage() {

  const Auth = useAuth()
  const user = Auth.getUser()

  const [userMe, setUserMe] = useState(null)
  const [modalOpen, setModalOpen] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [userEmailStatus, setUserEmailStatus] = useState({});

  useEffect(() => {
    async function fetchData() {
      setIsLoading(true)

      try {
        const response = await orderApi.getUserMe(user)
        setUserMe(response.data)
      } catch (error) {
        handleLogError(error)
      } finally {
        setIsLoading(false)
      }
    }

    fetchData()
  }, [user])

  const handlePasswordChange = (e, { name, value }) => {
    if (name === 'currentPassword') {
      setCurrentPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handlePasswordApply = () => {
    setModalOpen(false);
    setCurrentPassword('');
    setNewPassword('');
  };

  const handleUpdateUser = async (username) => {
    try {
      await orderApi.updateUser(user, username)
      handleGetUserMe()
    } catch (error) {
      handleLogError(error)
    }
  }

  const fetchUserMeData = async () => {
    setIsLoading(true)

    try {
      const response = await orderApi.getUserMe(user)
      setUserMe(response.data)
    } catch (error) {
      handleLogError(error)
    } finally {
      setIsLoading(false)
    }
  }

  const handleToggleUserEmail = async (username, isActive) => {
    try {
      await orderApi.toggleUserEmail(user, username, isActive)
      handleGetUserMe()
    } catch (error) {
      handleLogError(error)
    }
  }

  if (!Auth) {
    return <Navigate to='/login' />
  }

  return (
    <Container>
      <AccountSettingsTab
        userMe={userMe}
        handlePasswordChange={handlePasswordChange}
        handlePasswordApply={handlePasswordApply}
        currentPassword={currentPassword}
        newPassword={newPassword}
        modalOpen={modalOpen}
        setModalOpen={setModalOpen}
        handleUpdateUser={handleUpdateUser}
        handleToggleUserEmail={handleToggleUserEmail}
      />
    </Container>
  )
}

export default AccountSettingsPage
