import React, { useState } from 'react';
import { Grid, Form, Button, FormField, Accordion, FormCheckbox, GridColumn, Input, Modal, Image, Table, Segment } from 'semantic-ui-react';

function AccountSettingsForm({}) {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const handleChange = (e, { name, value }) => {
    if (name === 'currentPassword') {
      setCurrentPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handleApply = () => {
    setModalOpen(false);
    setCurrentPassword('');
    setNewPassword('');
  };

  const userImage = 'https://react.semantic-ui.com/images/avatar/large/daniel.jpg'; 
  const userInfo = {
    fullName: 'Иван Иванович Иванов', // Замените на реальное имя пользователя
    email: 'email@gmail.com', // Замените на реальный email пользователя
    phoneNumber: '+79876543210', // Замените на реальный номер телефона пользователя
    role: 'Пользователь', // Замените на реальную роль пользователя
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder={userInfo.email} />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder={userInfo.phoneNumber} />
            </Form.Field>
            <Form.Field>
              <a href='#' onClick={() => setModalOpen(true)}>Изменение пароля</a>
            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  return (
    <>
      <Grid stackable divided>
        <GridColumn>
          <Segment.Group horizontal>
            <Segment>
              <Image src={userImage} size='small' circular />
            </Segment>
            <Segment>
              <p><strong>ФИО:</strong> {userInfo.fullName}</p>
              <p><strong>Email:</strong> {userInfo.email}</p>
              <p><strong>Номер телефона:</strong> {userInfo.phoneNumber}</p>
              <p><strong>Роль:</strong> {userInfo.role}</p>
            </Segment>
          </Segment.Group>

          <Form>
            <Accordion as={FormField} panels={panels} />
          </Form>
        </GridColumn>
      </Grid>

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handleApply}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default AccountSettingsForm;
