import React, { useState } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import { updateUser } from '../misc/OrderApi'; // Импортируем метод updateUser из API

function AccountSettingsForm({ user }) {
  const [email, setEmail] = useState(user.email);
  const [phoneNumber, setPhoneNumber] = useState(user.phoneNumber);
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  const handleInputChange = (e, { name, value }) => {
    if (name === 'email') {
      setEmail(value);
    } else if (name === 'phoneNumber') {
      setPhoneNumber(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await OrderApi(user, { email, phoneNumber }); // Отправляем запрос на обновление данных пользователя
      setIsSuccess(true);
      setIsError(false);
      setErrorMessage('');
    } catch (error) {
      setIsError(true);
      setIsSuccess(false);
      setErrorMessage('Ошибка при обновлении данных пользователя.');
    }
  };

  return (
    <>
      <Form size='large' onSubmit={handleSubmit}>
        <Form.Input
          fluid
          name='email'
          icon='envelope'
          iconPosition='left'
          placeholder='Новый адрес электронной почты'
          value={email}
          onChange={handleInputChange}
        />
        <Form.Input
          fluid
          name='phoneNumber'
          icon='mobile alternate'
          iconPosition='left'
          placeholder='Новый номер телефона'
          value={phoneNumber}
          onChange={handleInputChange}
        />
        <Button color='teal' fluid size='large'>Сохранить изменения</Button>
      </Form>
      {isError && <Message negative>{errorMessage}</Message>}
      {isSuccess && <Message positive>Данные пользователя успешно обновлены.</Message>}
    </>
  );
}

export default AccountSettingsForm;
