import React, { useState } from 'react';
import { Accordion, Form, Button, Modal } from 'semantic-ui-react';

function ProfileSettingsAccordion({ userMe }) {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [modalOpen, setModalOpen] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'oldPassword') {
      setOldPassword(value);
    } else {
      setNewPassword(value);
    }
  };

  const handleSave = () => {
    // Здесь вы можете добавить логику сохранения нового пароля
    console.log("Old Password:", oldPassword);
    console.log("New Password:", newPassword);
    setModalOpen(false); // Закрываем модальное окно после сохранения
  };

  return (
    <Accordion styled fluid>
      <Accordion.Title>Настройки приватности</Accordion.Title>
      <Accordion.Content>
        <Form>
          <Form.Field>
            <label>Email</label>
            <input placeholder='' />
          </Form.Field>
          <Form.Field>
            <Form.Checkbox label='Присылать уведомления по Email' />
          </Form.Field>
          <Form.Field>
            <label>Номер телефона</label>
            <input placeholder='' />
          </Form.Field>
          <Form.Field>
            <a href="#" onClick={() => setModalOpen(true)}>Изменение пароля</a>
          </Form.Field>
          <Button>Применить</Button>
        </Form>
      </Accordion.Content>
      <Modal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        size="tiny"
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Старый пароль</label>
              <input
                type="password"
                name="oldPassword"
                value={oldPassword}
                onChange={handleChange}
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <input
                type="password"
                name="newPassword"
                value={newPassword}
                onChange={handleChange}
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button negative onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button positive onClick={handleSave}>Сохранить</Button>
        </Modal.Actions>
      </Modal>
    </Accordion>
  );
}

export default ProfileSettingsAccordion;
