import React, { useState } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import axios from 'axios';

function AccountSettingsForm({ user }) {
  const [formData, setFormData] = useState({
    email: user?.email || '',
    phoneNumber: user?.phoneNumber || ''
  });
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`/users/${user.username}`, formData);
      setIsSuccess(true);
      setIsError(false);
      setErrorMessage('');
    } catch (error) {
      setIsError(true);
      setIsSuccess(false);
      setErrorMessage('Ошибка при обновлении данных пользователя.');
    }
  };

  return (
    <>
      <Form size='large' onSubmit={handleSubmit}>
        <Form.Input
          fluid
          name='email'
          icon='envelope'
          iconPosition='left'
          placeholder='Новый адрес электронной почты'
          value={formData.email}
          onChange={handleInputChange}
        />
        <Form.Input
          fluid
          name='phoneNumber'
          icon='mobile alternate'
          iconPosition='left'
          placeholder='Новый номер телефона'
          value={formData.phoneNumber}
          onChange={handleInputChange}
        />
          <Form.Input
          fluid
          name='password'
          icon='lock'
          iconPosition='left'
          placeholder='Введите новый пароль'
          value={formData.phoneNumber}
          onChange={handleInputChange}
        />
        <Button color='teal' fluid size='large'>Сохранить изменения</Button>
      </Form>
      {isError && <Message negative>{errorMessage}</Message>}
      {isSuccess && <Message positive>Данные пользователя успешно обновлены.</Message>}
    </>
  );
}

export default AccountSettingsForm;
