import React, { useState } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import { updateUser } from '../misc/UserApi'; // Подставьте здесь ваш метод для обновления данных пользователя
import { handleLogError } from '../misc/Helpers';

function AccountSettingsForm () {

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [firstName, setFirstName] = useState('')
  const [fatherName, setFatherName] = useState('')
  const [lastName, setLastName] = useState('')
  const [email, setEmail] = useState('')
  const [phoneNumber, setPhoneNumber] = useState('')
  const [errorMessage, setErrorMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  const handleInputChange = (e, {name, value}) => {
    if (name === 'username') {
      setUsername(value)
    } else if (name === 'password') {
      setPassword(value)
    } else if (name === 'firstName') {
      setFirstName(value)
    } else if (name === 'fatherName') {
      setFatherName(value)
    } else if (name === 'lastName') {
      setLastName(value)
    } else if (name === 'email') {
      setEmail(value)
    } else if (name === 'phoneNumber') {
      setPhoneNumber(value)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Отправка запроса на обновление данных пользователя
      await updateUser({ email, phoneNumber });

      // Обновление статуса успешного обновления
      setIsSuccess(true);
      setIsError(false);
      setErrorMessage('');
    } catch (error) {
      handleLogError(error);
      // Обработка ошибок при обновлении данных
      setIsError(true);
      setIsSuccess(false);
      setErrorMessage('Ошибка при обновлении данных пользователя.');
    }
  };

  return (
    <>
      <Form size='large' onSubmit={handleSubmit}>
        <Form.Input
          fluid
          name='email'
          icon='envelope'
          iconPosition='left'
          placeholder='Новый адрес электронной почты'
          value={email}
          onChange={handleInputChange}
        />
        <Form.Input
          fluid
          name='phoneNumber'
          icon='mobile alternate'
          iconPosition='left'
          placeholder='Новый номер телефона'
          value={phoneNumber}
          onChange={handleInputChange}
        />
        <Button color='teal' fluid size='large'>Сохранить изменения</Button>
      </Form>
      {isError && <Message negative>{errorMessage}</Message>}
      {isSuccess && <Message positive>Данные пользователя успешно обновлены.</Message>}
    </>
  );
}

export default AccountSettingsForm;
