import React, { useState } from 'react';
import { Grid, Form, Button, Input, Table, Modal, Header } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';

function UserManageTable({ users, userUsernameSearch, handleInputChange, handleDeleteUser, handleSearchUser }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [toggleStates, setToggleStates] = useState({}); // Объект состояний для каждой кнопки "Выкл"

  const openModal = (user) => {
    setUserToDelete(user);
    setModalOpen(true);
  };

  const closeModal = () => {
    setUserToDelete(null);
    setModalOpen(false);
  };

  const toggleButton = (userId) => {
    setToggleStates((prevState) => ({
      ...prevState,
      [userId]: !prevState[userId], // Инвертирование состояния кнопки для конкретного пользователя
    }));
  };

  let userList;
  if (users.length === 0) {
    userList = (
      <Table.Row key='no-user'>
        <Table.Cell collapsing textAlign='center' colSpan='7'>Список пользователей пуст</Table.Cell>
      </Table.Row>
    );
  } else {
    userList = users.map((user) => {
      return (
        <Table.Row key={user.id}>
          <Table.Cell collapsing>
            <div>
              <Button
                color='red'
                attached='left'
                size='small'
                disabled={user.username === 'admin'}
                onClick={() => openModal(user)}
              >
                Удалить
              </Button>
              {/* Используется состояние toggleStates для определения цвета и текста кнопки */}
              <Button
                color={toggleStates[user.id] ? 'green' : 'yellow'} // Цвет кнопки меняется на зеленый при включенном состоянии
                size='small'
                attached='right'
                onClick={() => toggleButton(user.id)} // Изменение состояния кнопки при нажатии
              >
                {toggleStates[user.id] ? 'Вкл' : 'Выкл'} {/* Изменение текста кнопки в зависимости от состояния */}
              </Button>
            </div>
          </Table.Cell>
          <Table.Cell>{user.username}</Table.Cell>
          <Table.Cell>{user.lastName}</Table.Cell>
          <Table.Cell>{user.firstName}</Table.Cell>
          <Table.Cell>{user.fatherName}</Table.Cell>
          <Table.Cell>{user.email}</Table.Cell>
          <Table.Cell>{user.phoneNumber}</Table.Cell>
        </Table.Row>
      );
    });
  }

  return (
    <>
      <Grid stackable divided>
        <Grid.Row columns='2'>
          <Grid.Column width='4'>
            <Form onSubmit={handleSearchUser}>
              <Input
                action={{ icon: 'search' }}
                name='userUsernameSearch'
                placeholder='Поиск по «User ID» '
                value={userUsernameSearch}
                onChange={handleInputChange}
              />
            </Form>
          </Grid.Column>
          <Grid.Column>
            <NavLink to='/registration'>
              <Button positive size='large'>
                Добавить{' '}
              </Button>
            </NavLink>
          </Grid.Column>
        </Grid.Row>
      </Grid>
      <Table compact striped selectable>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell width={1} />
            <Table.HeaderCell width={1}>User ID</Table.HeaderCell>
            <Table.HeaderCell width={1}>Фамилия</Table.HeaderCell>
            <Table.HeaderCell width={1}>Имя</Table.HeaderCell>
            <Table.HeaderCell width={1}>Отчество</Table.HeaderCell>
            <Table.HeaderCell width={1}>Email</Table.HeaderCell>
            <Table.HeaderCell width={1}>Номер телефона</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>{userList}</Table.Body>
      </Table>
      <Modal open={modalOpen} onClose={closeModal} size='small'>
        <Header content='Подтверждение удаления' />
        <Modal.Content>
          <p>Вы уверены, что хотите удалить пользователя {userToDelete && userToDelete.username}?</p>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={closeModal}>
            Отмена
          </Button>
          <Button negative
            onClick={() => {
              handleDeleteUser(userToDelete.username);
              closeModal();
            }}
          >
            Подтвердить
          </Button>
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default UserManageTable;
