import React from 'react'
import { Tab } from 'semantic-ui-react'
import AccountSettingsForm from './AccountSettingsForm'

function AccountSettingsTab(props) {
  const { userMe, handleUpdateUser, handleToggleUserEmail, handlePasswordChange, handlePasswordApply } = props

  const panes = [
    {
      menuItem: { key: 'users', icon: 'cogs', content: 'Общая информация' },
      render: () => (
        <Tab.Pane >
          <AccountSettingsForm
            userMe={userMe}
            handlePasswordChange={handlePasswordChange}
            handlePasswordApply={handlePasswordApply}
            handleUpdateUser={handleUpdateUser}
            handleToggleUserEmail={handleToggleUserEmail}
          />
        </Tab.Pane>
      )
    },
  ]

  return (
    <Tab menu={{ borderless: true, attached: true, tabular: true }} panes={panes} />
  )
}

export default AccountSettingsTab
