import React from 'react';
import { Accordion, Form, Button, FormCheckbox } from 'semantic-ui-react';

function AccountPrivacyAccordion({ userMe, setModalOpen }) {
  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <a href="#" onClick={() => setModalOpen(true)}>Изменение пароля</a>
            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handlePasswordApply}
            positive
          />
        </Modal.Actions>
      </Modal>

  return ( <Accordion as={Form.Field} panels={panels} styled fluid />
  );
}

export default AccountPrivacyAccordion;
