import React from 'react';
import { 
  Grid, 
  Form, 
  Button, 
  FormField, 
  Accordion, 
  FormCheckbox, 
  GridColumn, 
  Input, 
  Modal, 
  Image, 
  Segment 
} from 'semantic-ui-react';

function AccountSettingsForm({ userMe, handleUpdateUser, handleToggleEmailNotifications, currentPassword, newPassword, handlePasswordChange, handlePasswordApply, modalOpen, setModalOpen }) {

  useEffect(() => { // Перенести в Page
    const activationStatus = {};
    users.forEach(user => {
      activationStatus[user.username] = user.isActive;
    });
    setUserActivationStatus(activationStatus);
    }, [users]);

  const userImage = 'https://react.semantic-ui.com/images/avatar/large/daniel.jpg'; 

  const panels = [ // Переместить в другой кмпонент
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
            {/* eslint-disable-next-line jsx-a11y/anchor-is-valid */}
            <a href="#" onClick={() => setModalOpen(true)}>Изменение пароля</a> 

            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  return (
    <>
      <Grid stackable divided>
        <GridColumn>
          <Segment.Group horizontal>
              <Image src={userImage} size='small' style={{ width: '185px', height: '185px', borderRadius: '0' }} />
            <Segment >
              <p><strong>ФИО: </strong> {userMe.lastName} {userMe.firstName} {userMe.fatherName}</p>
              <p><strong>Email: </strong> {userMe.email} </p>
              <p><strong>Уведомления: </strong> {userMe.isEmailSubscribed} Включены</p>
              <p><strong>Номер телефона: </strong> {userMe.phoneNumber}</p>
              <p><strong>Должность: </strong> {userMe.role}</p>
            </Segment>
          </Segment.Group>

          <Form>
            <Accordion 
              as={FormField} 
              panels={panels} 
              onClick={() => handleToggleEmailNotifications(userMe.username, setUserActivationStatus[userMe.username])}
            />
          </Form>
        </GridColumn>
      </Grid>

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handlePasswordApply}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default AccountSettingsForm;
