import React, { useEffect, useState } from 'react'
import { Navigate } from 'react-router-dom'
import { Container } from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { orderApi } from '../misc/OrderApi'
import { handleLogError } from '../misc/Helpers'
import AccountSettingsTab from './AccountSettingsTab'

function AccountSettingsPage() {
  const Auth = useAuth()
  const user = Auth.getUser()

  const [userMe, setUserMe] = useState(null)

  const handleUpdateUser = async (username) => {
    try {
      await orderApi.updateUser(user, username)
      handleGetUserMe()
    } catch (error) {
      handleLogError(error)
    }
  }

  const handleGetUserMe = async () => {
    try {
      const response = await orderApi.getUserMe(user)
      setUserMe(response.data)
    } catch (error) {
      handleLogError(error)
    } finally {
    }
  }

  const handleToggleUserEmail = async (username, isActive) => {
    try {
      await orderApi.toggleUserEmail(user, username, isActive)
      handleGetUserMe()
    } catch (error) {
      handleLogError(error)
    }
  }

  if (!Auth) {
    return <Navigate to='/login' />
  }

  return (
    <Container>
      <AccountSettingsTab
        handleUpdateUser={handleUpdateUser}
        handleToggleUserEmail={handleToggleUserEmail}
      />
    </Container>
  )
}

export default AccountSettingsPage
