import React, { useState } from 'react';
import { Form, Button, FormCheckbox, Modal, Input } from 'semantic-ui-react';
import AccountPrivacyAccordion from './AccountPrivacyAccordion';

function AccountSettingsForm({ userMe, handleUpdateUser, handleToggleEmailNotifications, userEmailStatus, currentPassword, newPassword, handlePasswordChange, handlePasswordApply }) {
  const [modalOpen, setModalOpen] = useState(false);

  return (
    <>
      <AccountPrivacyAccordion
        userMe={userMe}
        handlePasswordChange={handlePasswordChange}
        handlePasswordApply={handlePasswordApply}
        setModalOpen={setModalOpen}
      />

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handlePasswordChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handlePasswordApply}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default AccountSettingsForm;
