import React, { useState, useEffect } from 'react';
import { Grid, Form, Button, FormInput, FormGroup, FormField, Accordion, FormCheckbox, GridColumn, Segment, Input, Table, Modal, Header,   PlaceholderParagraph,
  PlaceholderLine,
  PlaceholderHeader,
  Placeholder, } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';


function AccountSettingsForm({ users, userUsernameSearch, handleInputChange, handleDeleteUser, handleToggleUserStatus, handleSearchUser }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [userActivationStatus, setUserActivationStatus] = useState({});

  const panels = [
    {
      key: 'details',
      title: 'Настройки приватности',
      content: {
        as: FormInput,
        label: 'Email',
        placeholder: 'myemail@gmail.com',
      },


    },
  ]

  return (
    <>
    <Grid stackable divided>
      <GridColumn  >
      <Form>
      <Placeholder>
        <PlaceholderHeader image >
          <PlaceholderLine />
          <PlaceholderLine />
        </PlaceholderHeader>
        <PlaceholderParagraph>
          <PlaceholderLine />
          <PlaceholderLine />
          <PlaceholderLine />
          <PlaceholderLine />
        </PlaceholderParagraph>
      </Placeholder>

      <Accordion as={FormField} panels={panels} />

      <Button positive>Применить</Button>
    </Form>
      </GridColumn>
    </Grid>
    </>
  );
}

export default AccountSettingsForm
