import React from 'react'
import { Link } from 'react-router-dom'
import { Container, Menu } from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'

function Navbar() {
  const { getUser, userIsAuthenticated, userLogout } = useAuth()

  const logout = () => {
    userLogout()
  }

  const enterMenuStyle = () => {
    return userIsAuthenticated() ? { "display": "none" } : { "display": "block" }
  }

  const logoutMenuStyle = () => {
    return userIsAuthenticated() ? { "display": "block" } : { "display": "none" }
  }

  const adminPageStyle = () => {
    const user = getUser()
    return user && user.data.rol[0] === 'ADMIN' ? { "display": "block" } : { "display": "none" }
  }

  const userPageStyle = () => {
    const user = getUser()
    return user && user.data.rol[0] === 'USER' || 'ADMIN' ? { "display": "block" } : { "display": "none" }
  }

  const getUserName = () => {
    const user = getUser()
    return user ? user.data.username : ''
  }

  return (
    <Menu inverted color='teal' stackable size='massive' style={{ borderRadius: 0 }}>
      <Container>

          <Menu.Item header>Theater Schedule</Menu.Item>
          <Menu.Item as={Link} exact='true' to="/about">О системе</Menu.Item>
          <Menu.Item as={Link} to="/userpage" style={userPageStyle()}>Просмотр расписания</Menu.Item>
          <Menu.Item as={Link} to="/adminpage" style={adminPageStyle()}>Управление расписанием</Menu.Item>

        <Menu.Menu position='right'>
          <Menu.Item as={Link} to="/login" style={enterMenuStyle()}>Войти</Menu.Item>
          <Menu.Item as={Link} to="/registration" style={enterMenuStyle()}>Регистрация</Menu.Item>
          <Menu.Item header style={logoutMenuStyle()}>{`Привет, ${getUserName()}`}</Menu.Item>
          <Menu.Item as={Link} to="/login" style={logoutMenuStyle()} onClick={logout}>Выход</Menu.Item>

        </Menu.Menu>
      </Container>
    </Menu>
  )
}

export default Navbar
