import React, { useState } from 'react';
import { Form, Button, Modal } from 'semantic-ui-react';

function ChangePasswordModal({ modalOpen, setModalOpen, oldPassword, setOldPassword, newPassword, setNewPassword, handleChangePassword }) {

  const openModal = (user) => { // Перенести в Page
    setUserToDelete(user);
    setModalOpen(true);
  };

  const closeModal = () => { // Перенести в Page
    setModalOpen(false);
  };

  return (
    <Modal onClose={closeModal} open={modalOpen}>
      <Modal.Header>Изменение пароля</Modal.Header>
      <Modal.Content>
        <Form>
          <Form.Field>
            <label>Старый пароль</label>
            <input
              type="password"
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
            />
          </Form.Field>
          <Form.Field>
            <label>Новый пароль</label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
          </Form.Field>
        </Form>
      </Modal.Content>
      <Modal.Actions>
        <Button onClick={handleClose}>Отмена</Button>
        <Button
          content="Подтвердить"
          onClick={() => {
            handleChangePassword(oldPassword, newPassword);
            handleClose();
          }}
          positive
        />
      </Modal.Actions>
    </Modal>
  );
}

export default ChangePasswordModal;
