import React, { useState } from 'react';
import { Accordion, Form, Button, FormCheckbox } from 'semantic-ui-react';

function ProfileSettingsAccordion({ userMe, setModalOpen }) {
  const [passwordChange, setPasswordChange] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const handlePasswordChange = () => {
    setPasswordChange(!passwordChange);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'oldPassword') {
      setOldPassword(value);
    } else {
      setNewPassword(value);
    }
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <a href="#" onClick={handlePasswordChange}>
                Изменение пароля
              </a>
            </Form.Field>
            {passwordChange && (
              <div>
                <Form.Field>
                  <label>Старый пароль</label>
                  <input
                    type="password"
                    name="oldPassword"
                    value={oldPassword}
                    onChange={handleChange}
                  />
                </Form.Field>
                <Form.Field>
                  <label>Новый пароль</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={newPassword}
                    onChange={handleChange}
                  />
                </Form.Field>
              </div>
            )}
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  return <Accordion as={Form.Field} panels={panels} styled fluid />;
}

export default ProfileSettingsAccordion;
