import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Container } from 'semantic-ui-react';
import UserManageTab from './UserManageTab';
import { orderApi } from '../misc/OrderApi';
import { handleLogError } from '../misc/Helpers';

function UserManagePage() {
  const [users, setUsers] = useState([]);
  const [userUsernameSearch, setUserUsernameSearch] = useState('');
  const [isAdmin, setIsAdmin] = useState(true);
  const [isUsersLoading, setIsUsersLoading] = useState(false);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await orderApi.getUsers();
        setUsers(response.data);
      } catch (error) {
        handleLogError(error);
      } finally {
        setIsUsersLoading(false);
      }
    };

    setIsAdmin(user.data.rol[0] === 'ADMIN');
    fetchUsers();
  }, []);

  const handleInputChange = (e, { name, value }) => {
    if (name === 'userUsernameSearch') {
      setUserUsernameSearch(value);
    }
  };

  const handleDeleteUser = async (username) => {
    try {
      await orderApi.deleteUser(username);
      setUsers(users.filter(user => user.username !== username));
    } catch (error) {
      handleLogError(error);
    }
  };

  const handleSearchUser = async () => {
    setIsUsersLoading(true);
    try {
      const response = await orderApi.getUsers(userUsernameSearch);
      setUsers(response.data);
    } catch (error) {
      handleLogError(error);
    } finally {
      setIsUsersLoading(false);
    }
  };

  const handleToggleFreezeAccount = async (user) => {
    try {
      await orderApi.toggleAccountFreeze(user.username);
      setUsers(users.map(u => u.username === user.username ? { ...u, accountFrozen: !u.accountFrozen } : u));
    } catch (error) {
      handleLogError(error);
    }
  };

  if (!isAdmin) {
    return <Navigate to='/' />;
  }

  return (
    <Container>
      <UserManageTab
        isUsersLoading={isUsersLoading}
        users={users}
        userUsernameSearch={userUsernameSearch}
        handleDeleteUser={handleDeleteUser}
        handleSearchUser={handleSearchUser}
        handleInputChange={handleInputChange}
        handleToggleFreezeAccount={handleToggleFreezeAccount}
      />
    </Container>
  );
}

export default UserManagePage;
