import React from 'react';
import { Accordion, Form, Button, Modal } from 'semantic-ui-react';

function ProfileSettingsAccordion({ userMe }) {
  const [open, setOpen] = React.useState(false);
  const [oldPassword, setOldPassword] = React.useState('');
  const [newPassword, setNewPassword] = React.useState('');

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'oldPassword') {
      setOldPassword(value);
    } else {
      setNewPassword(value);
    }
  };

  const handlePasswordChange = () => {
    // Здесь можно выполнить логику изменения пароля
    console.log("Старый пароль:", oldPassword);
    console.log("Новый пароль:", newPassword);
    // Закрываем модальное окно после изменения пароля
    handleClose();
  };

  return (
    <Accordion styled fluid>
      <Modal
        closeIcon
        open={open}
        trigger={<a href="#" onClick={handleOpen}>Изменение пароля</a>}
        onClose={handleClose}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Старый пароль</label>
              <input
                type="password"
                name="oldPassword"
                value={oldPassword}
                onChange={handleChange}
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <input
                type="password"
                name="newPassword"
                value={newPassword}
                onChange={handleChange}
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button color='black' onClick={handleClose}>
            Отмена
          </Button>
          <Button
            positive
            icon='checkmark'
            labelPosition='right'
            content='Изменить пароль'
            onClick={handlePasswordChange}
          />
        </Modal.Actions>
      </Modal>
    </Accordion>
  );
}

export default ProfileSettingsAccordion;
