import React, { useState } from 'react';
import { Form, Button, Modal } from 'semantic-ui-react';

function ProfileSettingsModal({ modalOpen, setModalOpen, modalClose, setModalClose, oldPassword, handleChangePassword, newPassword, setOldPassword, setNewPassword }) {

  return (
    <Modal onClose={modalClose} open={modalOpen}>
      <Modal.Header>Изменение пароля</Modal.Header>
      <Modal.Content>
        <Form>
          <Form.Field>
            <label>Старый пароль</label>
            <input
              type="password"
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
            />
          </Form.Field>
          <Form.Field>
            <label>Новый пароль</label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
          </Form.Field>
        </Form>
      </Modal.Content>
      <Modal.Actions>
        <Button onClick={setModalClose}>Отмена</Button>
        <Button
          content="Подтвердить"
          onClick={() => {
            handleChangePassword(oldPassword, newPassword);
            setModalClose();
          }}
          positive
        />
      </Modal.Actions>
    </Modal>
  );
}

export default ProfileSettingsModal;
