import React, { useState } from 'react';
import { 
  Grid, 
  Form, 
  Button, 
  FormField, 
  Accordion, 
  FormCheckbox, 
  GridColumn, 
  Input, 
  Modal, 
  Image, 
  Segment 
} from 'semantic-ui-react';
import { useAuth } from '../context/AuthContext';

function AccountSettingsForm({ getUser, handleToggleUserEmail, handleUpdateUser, }) {

  const [modalOpen, setModalOpen] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [userEmailStatus, setUserEmailStatus] = useState({});

  const getUsername = () => {
    const user = getUser()
    return user ? user.data.sub : ''
  }

  const handleChange = (e, { name, value }) => {
    if (name === 'currentPassword') {
      setCurrentPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handleApply = () => {
    setModalOpen(false);
    setCurrentPassword('');
    setNewPassword('');
  };

  const userImage = 'https://react.semantic-ui.com/images/avatar/large/daniel.jpg'; 

  const userInfo = {
    fullName: getUsername, 
    email: 'email@gmail.com', 
    phoneNumber: '+79876543210', 
    role: 'Актер', 
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder={userInfo.email} />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder={userInfo.phoneNumber} />
            </Form.Field>
            <Form.Field>
              <a href='#' onClick={() => setModalOpen(true)}>Изменение пароля</a>
            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  return (
    <>
      <Grid stackable divided>
        <GridColumn>
          <Segment.Group horizontal>
              <Image src={userImage} size='small' style={{ width: '185px', height: '185px', borderRadius: '0' }} />
            <Segment>
              <p><strong>ФИО:</strong> {userInfo.fullName}</p>
              <p><strong>Email:</strong> {userInfo.email}</p>
              <p><strong>Уведомления:</strong> Включены</p>
              <p><strong>Номер телефона:</strong> {userInfo.phoneNumber}</p>
              <p><strong>Должность:</strong> {userInfo.role}</p>
            </Segment>
          </Segment.Group>

          <Form>
            <Accordion as={FormField} panels={panels} />
          </Form>
        </GridColumn>
      </Grid>

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handleApply}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default AccountSettingsForm;
