import React, { useState } from 'react';
import { Grid, Form, GridColumn, Modal, FormField, Accordion, Button, Input } from 'semantic-ui-react';

function AccountSettingsForm({ users, userUsernameSearch, handleInputChange, handleDeleteUser, handleToggleUserStatus, handleSearchUser }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const handleChange = (e, { name, value }) => {
    if (name === 'currentPassword') {
      setCurrentPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handleApply = () => {
    // Здесь можно добавить логику для изменения пароля
    // например, отправка запроса на сервер
    setModalOpen(false);
    setCurrentPassword('');
    setNewPassword('');
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <Input placeholder='email@gmail.com' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <Input placeholder='+79876543210' />
            </Form.Field>
            <Button onClick={() => setModalOpen(true)}>Изменение пароля</Button>
          </Form>
        ),
      },
    },
  ];

  return (
    <>
      <Grid stackable divided>
        <GridColumn>
          <Form>
            <Accordion as={FormField} panels={panels} />
          </Form>
        </GridColumn>
      </Grid>

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button color='black' onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            labelPosition='right'
            icon='checkmark'
            onClick={handleApply}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default AccountSettingsForm;
