import React, { useState } from 'react';
import { Accordion, Form, Button, Modal } from 'semantic-ui-react';

function ProfileSettingsAccordion({ userMe, }) {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [modalOpen, setModalOpen] = useState(false);

  const handleChangePassword = () => {
    // Здесь добавьте логику для изменения пароля
    // Можете использовать oldPassword и newPassword
    // После успешного изменения пароля закройте модальное окно
    setModalOpen(false);
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <Form.Checkbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='' />
            </Form.Field>
            <Form.Field>
              <a href="#" onClick={() => setModalOpen(true)}>Изменение пароля</a>
            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];  

  return (
    <>
      <Accordion as={Form.Field} panels={panels} styled fluid />
      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Старый пароль</label>
              <input
                type="password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>
            Отмена
          </Button>
          <Button
            content="Подтвердить"
            icon='button'
            onClick={handleChangePassword}
            positive
          />
        </Modal.Actions>
      </Modal>
    </>
  );
}

export default ProfileSettingsAccordion;
