import React, { useState, useEffect } from 'react';
import { Grid, Form, Button, FormInput, FormGroup, FormField, Accordion, FormCheckbox, GridColumn, Segment, Input, Table, Modal, Header } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';


function AccountSettingsForm({ users, userUsernameSearch, handleInputChange, handleDeleteUser, handleToggleUserStatus, handleSearchUser }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [userActivationStatus, setUserActivationStatus] = useState({});

  const panels = [
    {
      key: 'details',
      title: 'Optional Details',
      content: {
        as: FormInput,
        label: 'Maiden Name',
        placeholder: 'Maiden Name',
      },
    },
  ]

  return (
    <>
    <Grid stackable divided>
      <GridColumn style={{ maxWidth: '1155px', margin: 'auto' }}>
      <Form>
      <FormGroup widths={2}>
        <FormInput label='First Name' placeholder='First Name' />
        <FormInput label='Last Name' placeholder='Last Name' />
      </FormGroup>
      <Accordion as={FormField} panels={panels} />

      <Button >Применить</Button>
    </Form>
      </GridColumn>
    </Grid>
    </>
  );
}

export default AccountSettingsForm
