import React, { useState, useEffect } from 'react';
import { Grid, Form, Button, Radio, FormInput, FormGroup, FormField, Accordion, FormCheckbox, GridColumn, Segment, Input, Table, Modal, Header,   PlaceholderParagraph,
  PlaceholderLine,
  PlaceholderHeader,
  Placeholder, } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';


function AccountSettingsForm({ users, userUsernameSearch, handleInputChange, handleDeleteUser, handleToggleUserStatus, handleSearchUser }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [userActivationStatus, setUserActivationStatus] = useState({});

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='email@gmail.com' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' name='privacy' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='+79876543210' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления в «Telegram»' name='privacy' />
            </Form.Field>

          </Form>
        ),
      },
    },
  ];

  return (
    <>
    <Grid stackable divided>
      <GridColumn  >
      <Form>
      <Placeholder>
        <PlaceholderHeader image >
          <PlaceholderLine />
          <PlaceholderLine />
        </PlaceholderHeader>
        <PlaceholderParagraph>
          <PlaceholderLine />
          <PlaceholderLine />
          <PlaceholderLine />
          <PlaceholderLine />
        </PlaceholderParagraph>
      </Placeholder>

      <Accordion as={FormField} panels={panels} />

      <Button positive>Применить</Button>
    </Form>
      </GridColumn>
    </Grid>
    </>
  );
}

export default AccountSettingsForm
