import React, { useEffect, useState } from 'react';
import { Container, Button, Input, Grid } from 'semantic-ui-react';
import UserManageTab from './UserManageTab';
import { OrderApi } from '../misc/OrderApi';

function UserManagePage() {
  const [users, setUsers] = useState([]);
  const [userUsernameSearch, setUserUsernameSearch] = useState('');
  const [isAdmin, setIsAdmin] = useState(true);
  const [isUsersLoading, setIsUsersLoading] = useState(false);

  useEffect(() => {
    // Implement your logic to check if user is admin here
    setIsAdmin(true);
    handleGetUsers();
  }, []);

  const handleInputChange = (e, { name, value }) => {
    if (name === 'userUsernameSearch') {
      setUserUsernameSearch(value);
    }
  };

  const handleGetUsers = async () => {
    setIsUsersLoading(true);
    try {
      // Call your API method to get users here
      // const response = await yourApiMethod();
      // setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setIsUsersLoading(false);
    }
  };

  const handleDeleteUser = async (username) => {
    try {
      // Call your API method to delete user here
      // await yourApiMethodToDeleteUser(username);
      handleGetUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleSearchUser = async () => {
    try {
      // Call your API method to search for users here
    } catch (error) {
      console.error('Error searching for users:', error);
    }
  };

  const handleToggleAccountStatus = async (username, isActive) => {
    try {
      // Call the toggleAccountStatus method from OrderApi here
      await OrderApi.toggleAccountStatus(user, username, isActive);
      handleGetUsers();
    } catch (error) {
      console.error('Error toggling account status:', error);
    }
  };

  if (!isAdmin) {
    // Implement redirection logic or display a message for non-admin users
    return null;
  }

  return (
    <Container>
      <UserManageTab
        isUsersLoading={isUsersLoading}
        users={users}
        userUsernameSearch={userUsernameSearch}
        handleDeleteUser={handleDeleteUser}
        handleSearchUser={handleSearchUser}
        handleInputChange={handleInputChange}
        handleToggleAccountStatus={handleToggleAccountStatus}
      />
    </Container>
  );
}

export default UserManagePage;
