import React, { useState } from 'react';
import { Button, Modal, Form } from 'semantic-ui-react';

const ChangePasswordModal = () => {
  const [open, setOpen] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleChange = (e, { name, value }) => {
    if (name === 'oldPassword') {
      setOldPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handleSubmit = () => {
    // Добавьте здесь логику для обработки отправки формы, например, вызов API для изменения пароля

    // После успешного изменения пароля закройте модальное окно
    handleClose();
  };

  return (
    <Modal
      onClose={handleClose}
      onOpen={handleOpen}
      open={open}
      trigger={<Button>Изменение пароля</Button>}
    >
      <Modal.Header>Изменение пароля</Modal.Header>
      <Modal.Content>
        <Form onSubmit={handleSubmit}>
          <Form.Input
            label="Старый пароль"
            type="password"
            name="oldPassword"
            value={oldPassword}
            onChange={handleChange}
            required
          />
          <Form.Input
            label="Новый пароль"
            type="password"
            name="newPassword"
            value={newPassword}
            onChange={handleChange}
            required
          />
          <Modal.Actions>
            <Button color='black' onClick={handleClose}>
              Отмена
            </Button>
            <Button
              type='submit'
              content="Подтвердить"
              positive
            />
          </Modal.Actions>
        </Form>
      </Modal.Content>
    </Modal>
  );
};

export default ChangePasswordModal;
