import React from 'react'
import { Tab } from 'semantic-ui-react'
import UserManageTable from './UserManageTable'

function UserManageTab(props) {
  const { handleInputChange, isUsersLoading, users, userUsernameSearch, handleDeleteUser, handleSearchUser, handleToggleAccountStatus } = props

  const panes = [
    {
      menuItem: { key: 'users', icon: 'users', content: 'Список пользователей' },
      render: () => (
        <Tab.Pane loading={isUsersLoading}>
          <UserManageTable
            users={users}
            userUsernameSearch={userUsernameSearch}
            handleInputChange={handleInputChange}
            handleDeleteUser={handleDeleteUser}
            handleSearchUser={handleSearchUser}
            handleToggleAccountStatus ={handleToggleAccountStatus}
          />
        </Tab.Pane>
      )
    }
  ]

  return (
    <Tab menu={{ attached: 'top' }} panes={panes} />
  )
}

export default UserManageTab
