import React, { useState } from 'react';
import { Form, Button, Message, Radio } from 'semantic-ui-react';
import axios from 'axios';

function AccountSettingsForm({ user }) {
  const [formData, setFormData] = useState({
    email: user?.email || '',
    phoneNumber: user?.phoneNumber || '',
    subscribeToEmails: user?.subscribeToEmails || false
  });
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`/users/${user.username}`, formData);
      setIsSuccess(true);
      setIsError(false);
      setErrorMessage('');
    } catch (error) {
      setIsError(true);
      setIsSuccess(false);
      setErrorMessage('Ошибка при обновлении данных пользователя.');
    }
  };
  return (
      <Grid textAlign='center'>
        <Grid.Row>
        <Grid.Column style={{ maxWidth: 450 }}>
            <Segment >
              <Statistic>
              <Form size='large' onSubmit={handleSubmit}>
        <Form.Input
          fluid
          name='email'
          icon='envelope'
          iconPosition='left'
          placeholder='Введите адрес электронной почты'
          value={formData.email}
          onChange={handleInputChange}
        />
        <Radio
          toggle
          name='subscribeToEmails'
          label='Подписаться на рассылку по электронной почте'
          checked={formData.subscribeToEmails}
          onChange={handleInputChange}
        />
        <Form.Input
          fluid
          name='phoneNumber'
          icon='mobile alternate'
          iconPosition='left'
          placeholder='Введите номер телефона'
          value={formData.phoneNumber}
          onChange={handleInputChange}
        />
         <Form.Input
          fluid
          name='password'
          icon='lock'
          iconPosition='left'
          placeholder='Введите новый пароль'
          value={formData.password}
          onChange={handleInputChange}
        />
        <Button color='teal' fluid size='large'>Сохранить изменения</Button>
      </Form>
      {isError && <Message negative>{errorMessage}</Message>}
      {isSuccess && <Message positive>Данные пользователя успешно обновлены.</Message>}
              </Statistic>
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
  )

}

export default AccountSettingsForm;
