import React, { useState } from 'react';
import { Grid, Form, Button, FormField, Accordion, FormCheckbox, GridColumn, Input, Modal, Card, Icon, Image, CardContent, CardHeader, CardMeta, CardDescription } from 'semantic-ui-react';

function AccountSettingsForm({}) {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const handleChange = (e, { name, value }) => {
    if (name === 'currentPassword') {
      setCurrentPassword(value);
    } else if (name === 'newPassword') {
      setNewPassword(value);
    }
  };

  const handleApply = () => {
    setModalOpen(false);
    setCurrentPassword('');
    setNewPassword('');
  };

  const panels = [
    {
      key: 'privacy',
      title: 'Настройки приватности',
      content: {
        content: (
          <Form>
            <Form.Field>
              <label>Email</label>
              <input placeholder='email@gmail.com' />
            </Form.Field>
            <Form.Field>
              <FormCheckbox label='Присылать уведомления по Email' />
            </Form.Field>
            <Form.Field>
              <label>Номер телефона</label>
              <input placeholder='+79876543210' />
            </Form.Field>
            <Form.Field>
              <a href='#' onClick={() => setModalOpen(true)}>Изменение пароля</a>
            </Form.Field>
            <Button>Применить</Button>
          </Form>
        ),
      },
    },
  ];

  return (
    <>
      <Grid stackable divided>
        <GridColumn>
          <Form>
            <Accordion as={FormField} panels={panels} />
          </Form>
        </GridColumn>
      </Grid>

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
      >
        <Modal.Header>Изменение пароля</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Текущий пароль</label>
              <Input
                name='currentPassword'
                value={currentPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
            <Form.Field>
              <label>Новый пароль</label>
              <Input
                name='newPassword'
                value={newPassword}
                onChange={handleChange}
                type='password'
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Отмена</Button>
          <Button
            content='Применить'
            onClick={handleApply}
            positive
          />
        </Modal.Actions>
      </Modal>

      {/* Добавление изображения */}
      <Card>
        <Image src='https://react.semantic-ui.com/images/avatar/large/daniel.jpg' wrapped ui={false} />
        <CardContent>
          <CardHeader>Daniel</CardHeader>
          <CardMeta>Joined in 2016</CardMeta>
          <CardDescription>
            Daniel is a comedian living in Nashville.
          </CardDescription>
        </CardContent>
        <CardContent extra>
          <a>
            <Icon name='user' />
            10 Friends
          </a>
        </CardContent>
      </Card>
    </>
  );
}

export default AccountSettingsForm;
